mapper.select:
/* getgagevueList 가계부 캘린더/리스트 page - 가계부 조회 */ SELECT main.day, mn_no, mbr_no, mn_dtm, mn_use_dvs, ( SELECT MAX(detail_name) FROM tb_detail_code WHERE mn_use_dvs = detail_code ) AS mn_use_dvs_name, mn_pay_dvs, mn_use_dvs_det, ( SELECT MAX(detail_name) FROM tb_detail_code WHERE mn_use_dvs_det = detail_code ) AS mn_use_dvs_det_name, mn_use_memo, mn_rgst_dtm, mn_rgst_id, mn_upd_dtm, mn_upd_id, mn_amount, ( SELECT SUM(CASE WHEN mn_use_dvs = '1' AND mn_rgst_id = #{mn_rgst_id} THEN mn_amount ELSE 0 END) FROM tb_mn WHERE 1=1
AND DATE(mn_dtm) BETWEEN #{from_date} AND #{to_date}
) AS sum_for_use_dvs_expense_sum, ( SELECT SUM(CASE WHEN mn_use_dvs = '2' AND mn_rgst_id = #{mn_rgst_id} THEN mn_amount ELSE 0 END) FROM tb_mn WHERE 1=1
AND DATE(mn_dtm) BETWEEN #{from_date} AND #{to_date}
) AS sum_for_use_dvs_import_sum FROM ( SELECT DISTINCT DATE(mn_dtm) AS day FROM tb_mn WHERE 1=1
AND DATE(mn_dtm) BETWEEN #{from_date} AND #{to_date}
) main JOIN tb_mn ON DATE(tb_mn.mn_dtm) = main.day WHERE 1=1 and mn_rgst_id = #{mn_rgst_id} ORDER BY mn_dtm

		/*gagevueListCnt 가계부 캘린더/리스트 page - 가계부 목록 건수 */
		SELECT COUNT(*) as totalCnt
		FROM tb_mn
    	WHERE 1=1

		 AND DATE(mn_dtm) BETWEEN #{from_date} AND #{to_date} 

		AND mn_rgst_id = #{mn_rgst_id}
		ORDER BY mn_dtm DESC

		/*gagevueListCnt 가계부 캘린더/리스트 page - 회원관리번호 채번 */
		SELECT mbr_no
		FROM tb_userinfo
    	WHERE 1=1
		AND loginID = #{loginID}

mapper.update:
/* gagevueUpdate 가계부 리스트 page - 수정 */
UPDATE tb_mn
SET		mn_use_dvs = #{mn_use_dvs}
		,	mn_pay_dvs = #{mn_pay_dvs}
		,	mn_amount = #{mn_amount}
		,	mn_use_dvs_det = #{mn_use_dvs_det}
		,	mn_use_memo = #{mn_use_memo}
		,	mn_upd_dtm = NOW()
WHERE	mn_no = #{mn_no}

mapper.select:
/* gagevueListTotalCnt 가계부 리스트 page - 수입/지출 토탈 조회 */
 SELECT
 	mn_no,
    DATE(mn_dtm) AS day,
    SUM(CASE WHEN mn_use_dvs = '1' THEN mn_amount ELSE 0 END) AS sum_for_use_dvs_expense,
    SUM(CASE WHEN mn_use_dvs = '2' THEN mn_amount ELSE 0 END) AS sum_for_use_dvs_import
FROM tb_mn
WHERE 1=1

 AND DATE(mn_dtm) BETWEEN #{from_date} AND #{to_date} 

AND mn_rgst_id = #{mn_rgst_id}
GROUP BY day
ORDER BY day;

mapper.delete:
/*deletegagevue 가계부 캘린더/리스트 page - 삭제  */
DELETE
FROM 	tb_mn
WHERE 	mn_no = #{mn_no}
